import React, { useState } from 'react';
import axios from 'axios';

function Payments() {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    houseNumber: '',
    amount: '',
    purpose: 'Maintenance',
  });

  const handleChange = (e) => {
    setFormData(prev => ({ ...prev, [e.target.name]: e.target.value }));
  };

  const handlePayment = async (e) => {
    e.preventDefault();

    const res = await axios.post('http://localhost:5000/api/payment/create-order', formData);
    const { orderId, key } = res.data;

    const options = {
      key,
      amount: formData.amount * 100,
      currency: 'INR',
      name: 'Smart Society',
      description: `Payment for ${formData.purpose}`,
      order_id: orderId,
      handler: async (response) => {
        await axios.post('http://localhost:5000/api/payment/verify', {
          razorpayOrderId: response.razorpay_order_id,
          razorpayPaymentId: response.razorpay_payment_id,
          razorpaySignature: response.razorpay_signature,
        });
        alert('Payment Successful!');
      },
      prefill: {
        name: formData.name,
        email: formData.email,
      },
      theme: { color: "#3399cc" },
    };

    const rzp = new window.Razorpay(options);
    rzp.open();
  };

  return (
    <form onSubmit={handlePayment} style={{ maxWidth: 500, margin: '0 auto' }}>
      <h2>Make a Payment</h2>
      <input name="name" placeholder="Name" onChange={handleChange} required />
      <input name="email" placeholder="Email" onChange={handleChange} required />
      <input name="houseNumber" placeholder="House Number" onChange={handleChange} required />
      <input name="amount" type="number" placeholder="Amount (INR)" onChange={handleChange} required />
      <select name="purpose" onChange={handleChange}>
        <option value="Maintenance">Maintenance</option>
        <option value="Fine">Fine</option>
        <option value="Rent">Rent</option>
      </select>
      <button type="submit">Pay Now</button>
    </form>
  );
}

export default Payments;
