// src/pages/Dashboard.js
import React, { useEffect, useState } from 'react';

import './Dashboard.css';
import { useNavigate } from 'react-router-dom';

const Dashboard = () => {
  const navigate = useNavigate();
const [dashboardData, setDashboardData] = useState([]);
  const [flatInfo, setFlatInfo] = useState({ owned: 0, rented: 0, empty: 0, total: 0 });
const [username, setUsername] = useState('');
const [email, setEmail] = useState('');
const [password, setPassword] = useState('');
const [houseNumber, setHouseNumber] = useState('');

 
  const features = [
    { title: 'Payments', icon: 'üí≥', route: '/payments' },
    { title: 'Complaints', icon: 'üìù', route: '/complaints' },
    { title: 'Emergency Alerts', icon: 'üö®', route: '/emergency-alerts' },
    { title: 'Visitor Tracking', icon: 'üïµÔ∏è‚Äç‚ôÇÔ∏è', route: '/visitor-tracking' },
    { title: 'Polling', icon: 'üó≥Ô∏è', route: '/polling' },
    { title: 'Parking', icon: 'üöó', route: '/parking' },
    { title: 'Courier Alerts', icon: 'üì¶', route: '/courier-alerts' },
    { title: 'Language Support', icon: 'üåê', route: '/language-support' }
  ];
useEffect(() => {
    const token = localStorage.getItem('token');

    // Fetch logged-in user details
    axios.get('http://localhost:5000/api/dashboard', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
    .then((res) => {
      setDashboardData(res.data);
    })
    .catch((err) => {
      console.error('Dashboard fetch error:', err);
    });

    // Fetch flat statistics
    axios.get('http://localhost:5000/api/flats', {
      headers: {
        Authorization: `Bearer ${token}`,
      },
    })
    .then((res) => {
      setFlatStats(res.data);
    })
    .catch((err) => {
      console.error('Flat stats fetch error:', err);
    });
  }, []);
return(
  <div className="dashboard-container">
      <h1 className="dashboard-heading">Welcome to Your Dashboard</h1>
      <h3>{dashboardData.user.username}</h3>
      <p>Your Flat Number: {dashboardData.user.houseNumber}</p>

      <div className="flat-summary">
        <div className="summary-card">üè¢ Total Flats: {flatStats.total}</div>
        <div className="summary-card">üè† Owned: {flatStats.owned}</div>
        <div className="summary-card">üè° Rented: {flatStats.rented}</div>
        <div className="summary-card">üï≥Ô∏è Empty: {flatStats.empty}</div>
      </div>

      <div className="dashboard-grid">
        {features.map((feature, index) => (
          <div
            key={index}
            className="dashboard-card"
            onClick={() => navigate(feature.route)}
          >
            <div className="card-icon">{feature.icon}</div>
            <div className="card-title">{feature.title}</div>
          </div>
        ))}
      </div>
    </div>
  );
};


export default Dashboard;
