          <h3>Available Flats</h3>
          <p>View flat status & apply</p>
        </div>
        <div className="dashboard-card" onClick={() => alert("Events page coming soon")}>
          <h3>Upcoming Events</h3>
          <p>Community functions & updates</p>
        </div>
        <div className="dashboard-card" onClick={() => alert("Payments section coming soon")}>
          <h3>Pay Maintenance</h3>
          <p>View dues & make payments</p>
        </div>
        <div className="dashboard-card" onClick={() => alert("Announcements page coming soon")}>
          <h3>Important News</h3>
          <p>Latest society notices</p>
        </div>
      </section>
    </div>
